import React, { memo, useEffect, useState } from "react";
import { csv } from "d3-fetch";
import {
    ZoomableGroup,
    ComposableMap,
    Geographies,
    Geography
} from "react-simple-maps";
import { styled } from '@mui/material/styles';
import Slider from '@mui/material/Slider';
import Box from '@mui/material/Box';

const geoUrl =
    "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json"; 

const marks = [
    {
        value: 10,
        label: '1990',
    },
    {
        value: 40,
        label: '2000',
    },
    {
        value: 70,
        label: '2010',
    },
    {
        value: 85,
        label: '2015',
    },
    {
        value: 100,
        label: '2020',
    },
];

function valuetext(label) {
    return label;
}

function valueLabelFormat(value) {
    return marks.findIndex((mark) => mark.value === value) + 1;
}

const MapChart = ({ setTooltipContent }) => {

    const [forestData, setForestData] = useState([]);
    const [estimates, setEstimates] = useState([]);
    const [animals, setAnimals] = useState([]);
    const [year, setYear] = useState([]);

    useEffect(() => {
        csv(`/annual-change-forest-area.csv`).then((forestData) => {
            setForestData(forestData);
        });
        csv(`/living-planet-index-by-region.csv`).then((estimates) => {
            setEstimates(estimates);
        });
        csv(`/redlist-animals-by-country.csv`).then((animals) => {
            setAnimals(animals);
        });
    },
      []);

    const handleChange = (event, newValue) => {
        setYear((((newValue - 10)/3) + 1990));
    }

    return (
        <>
            <ComposableMap data-tip="" projectionConfig={{ scale: 200 }}>
                <ZoomableGroup>
                    <Geographies geography={geoUrl}>
                        {({ geographies }) =>
                            geographies.map((geo) => {
                                const deforestation = forestData.find((s) => s.ISO3 === geo.properties.ISO_A3 && s.Year === year.toString());
                                const est = estimates.find((s) => s.Region === geo.properties.CONTINENT);
                                const animalRedlist = animals.find((s) => s.Name)
                                return (
                                    <Geography
                                        key={geo.rsmKey}
                                        geography={geo}
                                        onMouseEnter={() => {
                                            const { NAME } = geo.properties;
                                            ((deforestation && year.toString() === `2020`)
                                                ? setTooltipContent(`${NAME}<br />
                                                                         ------------<br />
                                                                         Year: ` + year + `<br />
                                                                         Biodiversity loss: ${est["Percent"]} <br />
                                                                         Forest loss: ${deforestation["Net-forest-conversion"]} hectares`   
                                                )
                                                : (!deforestation && (year.toString() === `2020`)) ?
                                                        setTooltipContent(`${NAME}<br />
                                                                         ------------<br />
                                                                         Year: ` + year + `<br />
                                                                         Biodiversity loss: ${est["Percent"]} <br />
                                                                         Forest loss: No data for ` + year
                                                )
                                                : (deforestation && year.toString() !== `2020`) ?
                                                        setTooltipContent(`${NAME}<br />
                                                                         ------------<br />
                                                                         Year: ` + year + `<br />
                                                                         Biodiversity loss: No biodiversity data for ` + year + ` <br />
                                                                         Forest loss: ${deforestation["Net-forest-conversion"]} hectares`
                                                )
                                                : setTooltipContent(`No data found for ${NAME} in ` + year));
                                        }}
                                        onMouseLeave={() => {
                                            setTooltipContent("");
                                        }}
                                        style={{
                                            default: {
                                                fill: "#D6D6DA",
                                                outline: "#000000",
                                            },
                                            hover: {
                                                fill: "#F53",
                                                outline: "#000000"
                                            },
                                            pressed: {
                                                fill: "#E42",
                                                outline: "#000000"
                                            }
                                        }}
                                    />
                                );
                            })
                        }
                    </Geographies>
                </ZoomableGroup>
            </ComposableMap>
            <Box sx={{ width: 1000 }, { ml: 10 }}>
                <Slider
                    onChange={handleChange}
                    track={false}
                    aria-label="Restricted values"
                    defaultValue={100}
                    valueLabelFormat={valueLabelFormat}
                    getAriaValueText={valuetext}
                    step={null}
                    marks={marks}
                 />
            </Box>
        </>
    );
};

export default memo(MapChart);
