import React, { memo, useEffect, useState } from "react";
import { csv } from "d3-fetch";
import {
    ZoomableGroup,
    ComposableMap,
    Geographies,
    Geography
} from "react-simple-maps";
import Slider from '@mui/material/Slider';

const geoUrl =
    "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

const rounded = num => {
    if (num > 1000000000) {
        return Math.round(num / 100000000) / 10 + "Bn";
    } else if (num > 1000000) {
        return Math.round(num / 100000) / 10 + "M";
    } else {
        return Math.round(num / 100) / 10 + "K";
    }
}; 

const trackValues = [
    {
        value: 1990,
        label: '1990',
    },
    {
        value: 2000,
        label: '2000',
    },
    {
        value: 2010,
        label: '2010',
    },
    {
        value: 2015,
        label: '2015',
    },
    {
        value: 2020,
        label:'2020',
    },
];

const MapChart = ({ setTooltipContent }) => {

        const [forestData, setForestData] = useState([]);
        const [estimates, setEstimates] = useState([]);

    useEffect(() => {
        csv(`/annual-change-forest-area.csv`).then((forestData) => {
            setForestData(forestData);
        });
        csv(`/living-planet-index-by-region.csv`).then((estimates) => {
            setEstimates(estimates);
        })
    },
      []);


    return (
        <>
            <ComposableMap data-tip="" projectionConfig={{ scale: 200 }}>
                <ZoomableGroup>
                    <Geographies geography={geoUrl}>
                        {({ geographies }) =>
                            geographies.map((geo) => {
                                const fifteen = forestData.find((s) => s.ISO3 === geo.properties.ISO_A3);
                                const est = estimates.find((s) => s.Region === geo.properties.CONTINENT);
                                return (
                                    <Geography
                                        key={geo.rsmKey}
                                        geography={geo}
                                        onMouseEnter={() => {
                                            const { NAME, POP_EST } = geo.properties;
                                            (true
                                                ? setTooltipContent(`${NAME}<br />
                                                                         ------------<br />
                                                                         Year: 2020<br />
                                                                               Biodiversity loss: ${est["Percent"]}` )
                                                : setTooltipContent(`No data found for ${NAME}`));
                                        }}
                                        onMouseLeave={() => {
                                            setTooltipContent("");
                                        }}
                                        style={{
                                            default: {
                                                fill: "#D6D6DA",
                                                outline: "#000000",
                                            },
                                            hover: {
                                                fill: "#F53",
                                                outline: "#000000"
                                            },
                                            pressed: {
                                                fill: "#E42",
                                                outline: "#000000"
                                            }
                                        }}
                                    />
                                );
                            })
                        }
                    </Geographies>
                </ZoomableGroup>
            </ComposableMap>
            <Slider
                track={false}
                aria-labelledby="track-false-slider"
                getAriaValueText={valuetext}
                defaultValue={30}
                marks={marks}
            />
        </>
    );
};

export default memo(MapChart);
